import React, {useState} from "react";
import "./App.css";
import { fakeData } from "./fakeData.js";
 
console.log(fakeData);

function App() {
  const [day, setDay] = useState(1);
  return (
    <div className="App">



      <div className="content">
        <h1>Photo of the day-NASA</h1>
        <div className="buttons">
           <button onClick={()=>setDay(1)} className={day===1? 'selected': ''}>Bugün</button>
           <button onClick={()=>setDay(2)} className={day===2? 'selected': ''}>Dün</button>
           <button onClick={()=>setDay(3)} className={day===3? 'selected': ''}>Geçen Hafta</button>
          
        </div>
        
        <div className="photo">
        <div className="photo-img">
          <img src={fakeData.url}  alt={`APOD of the day=${fakeData.date}`}/>

          Buraya Günün Fotoğrafı Gelecek
        </div> 
        <div className="photo-info">
          <h2>{fakeData.title}</h2>
          <div>{fakeData.date}</div>
          <p>{fakeData.explanation}</p>
        <div>Buraya foroğraf bilgisi gelecek
        </div>
         </div>

      </div>
      </div>
    </div>
  );
}

export default App;
