import React, {useState} from "react";
import "./App.css";
import { fakeData } from "./fakeData.js";
 
console.log(fakeData);

function App() {
  return (
    <div className="App">
const [day,setDay] = useState(1);


      <div className="content">
        <h1>Photo of the day-NASA</h1>
        <div className="buttons">
          <button className={day===1?'selected': ''}>Bugün</button>
          <button className={day===2?'selected': ''}>Bugün</button>
          <button className={day===3 ?'selected': ''}>Bugün</button>
          <button>Dün</button>
          <button>Geçen Hafta</button>
        </div>
        
        <div className="photo">
        <div className="photo-img">
          <img src={fakeData.url}  alt={`APOD of the day=${fakeData.date}`}/>

          Buraya Günün Fotoğrafı Gelecek
        </div> 
        <div className="photo-info">
          <h2>{fakeData.title}</h2>
          <div>{fakeData.date}</div>
          <p>{fakeData.explanation}</p>
        <div>Buraya foroğraf bilgisi gelecek
        </div>
         </div>

      </div>
      </div>
    </div>
  );
}

export default App;
